<?php

namespace lispa\amos\projectmanagement\models\base;

use lispa\amos\projectmanagement\Module;
use Yii;
use yii\db\ActiveRecord;

/**
 * This is the base-model class for table "projects_tasks_joined_organization_mm".
 *
 * @property integer $id
 * @property integer $projects_tasks_id
 * @property integer $organization_id
 * @property string $created_at
 * @property string $updated_at
 * @property string $deleted_at
 * @property integer $created_by
 * @property integer $updated_by
 * @property integer $deleted_by
 *
 * @property \lispa\amos\projectmanagement\models\ProjectsTasks $projects_tasks
 */
class ProjectsTasksJoinedOrganizationsMm extends ActiveRecord
{


    /**
     * @inheritdoc
     */
    public static function tableName()
    {
        return 'projects_tasks_joined_organization_mm';
    }

    /**
     * @inheritdoc
     */
    public function rules()
    {
        return [
            [['projects_tasks_id', 'organization_id'], 'required'],
            [['projects_tasks_id', 'organization_id', 'created_by', 'updated_by', 'deleted_by'], 'integer'],
            [['created_at', 'updated_at', 'deleted_at'], 'safe'],
            [
                ['organization_id'],
                'exist',
                'skipOnError' => true,
                'targetClass' => $this->organizationModelClass,
                'targetAttribute' => ['organization_id' => 'id']
            ],
            [
                ['projects_tasks_id'],
                'exist',
                'skipOnError' => true,
                'targetClass' => \lispa\amos\projectmanagement\models\ProjectsTasks::className(),
                'targetAttribute' => ['projects_tasks_id' => 'id']
            ],
        ];
    }

    public $organizationModelClass;

    public function init()
    {
        parent::init(); // TODO: Change the autogenerated stub

        $this->organizationModelClass = Module::getModuleOrganization()->getOrganizationModelClass();
    }

    /**
     * @inheritdoc
     */
    public function attributeLabels()
    {
        return [
            'id' => 'ID',
            'projects_tasks_id' => 'Projects ID',
            'organization_id' => 'Organization ID',
            'created_at' => 'Creato il',
            'updated_at' => 'Aggiornato il',
            'deleted_at' => 'Cancellato il',
            'created_by' => 'Creato da',
            'updated_by' => 'Aggiornato da',
            'deleted_by' => 'Cancellato da',
        ];
    }

    /**
     * @return \yii\db\ActiveQuery
     */
    public function getOrganization()
    {
        return $this->hasOne($this->organizationModelClass,
            ['id' => 'organization_id'])->inverseOf('projectsTasksJoinedOrganizationsMms');
    }

    /**
     * @return \yii\db\ActiveQuery
     */
    public function getProjectsTasks()
    {
        return $this->hasOne(\lispa\amos\projectmanagement\models\ProjectsTasks::className(),
            ['id' => 'projects_tasks_id'])->inverseOf('p1rojectsJoinedOrganizationsMms');
    }
}
